import { Checkbox, InputLabel, ListItemText, Menu, MenuItem, OutlinedInput, Select, SelectChangeEvent } from '@mui/material';
import React, { useState } from 'react';
import diagnoses from '../../data';
import { Diagnosis } from '../../types';

function Common() {

     const [description,setDescription]=useState('')
     const [date,setDate]=useState('')
     const [specialist,setSpecialist]=useState('')
     const [selectedCodes,setSelectedCodes]= useState<Array<Diagnosis['code']>>([])

     const codes = diagnoses.map(disease => disease.code)
     console.log("selectedCodes: ", selectedCodes)

     const setProperty=()=>{

     }

       const handleChange = (event: SelectChangeEvent<typeof selectedCodes>) => {
    const {
      target: { value },
    } = event;
    setSelectedCodes(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(',') : value,
    );
  };

  return (
    <div>
      <br/>
      <div>Description:<input name="description" type="text"/></div>
      <br/>
      <div>Date:<input name="date" type="date"/></div>
      <br/>
      <div>Specialist:<input name="specialist" type="text"/></div>
      <br/>
      <div>
         <InputLabel id="demo-multiple-name-label">Diagnostic Codes</InputLabel>
        {<Select
        sx={{height:"40vh",overflow:'scroll'}}
          labelId="demo-multiple-name-label"
          id="demo-multiple-name"
          multiple
          value={selectedCodes}
          onChange={handleChange}
          input={<OutlinedInput  label="Tag" />}
          renderValue={(selected) => selected.join(', ')}

          >
        {codes.map((code) => (
                  <MenuItem key={code} value={code}>
                    <Checkbox checked={selectedCodes.indexOf(code) > -1} />
                    <ListItemText primary={code} />
                  </MenuItem>
                ))
          }
        </Select>

        }
      </div>
    </div>
  )
}

export default Common
