import React, { useState } from 'react'

interface propTypes{
  date:string,
  criteria:string
}

function Hospital() {

  const [discharge,setDischarge] =useState<propTypes>({
    date:'',
    criteria:''
  })

  const setDate = (date:string) =>{
    setDischarge({...discharge,date})
  }

  const setCriteria = (criteria:string) =>{
    setDischarge({...discharge,criteria})
  }
  return (
    <div>
      <input onChange={({target})=>setDate(target.value)} type='date'/>
      <input onChange={({target})=>setCriteria(target.value)} type='text'/>
    </div>
  )
}

export default Hospital
